          className: { $exists: true, $nin: [null, ""] },
        },
      },
      {
        $group: {
          _id: "$className",
          studentCount: { $sum: 1 },
        },
      },
      {
        $sort: { _id: 1 },
      },
    ]);

    return res.json({
      classes: classes.map((item) => ({
        name: item._id,
        studentCount: item.studentCount,
      })),
    });
  } catch (error) {
    console.error("Failed to list classes for teacher:", error);
    return res.status(500).json({ error: "Failed to load classes." });
